INSERT INTO public.site_content (id, value, description) VALUES
  ('cookie_consent_text', 'Мы используем файлы cookie для улучшения работы сайта. Продолжая использовать сайт, вы соглашаетесь с использованием cookie.', 'Текст предупреждения о куках'),
  ('privacy_policy_title', 'Политика конфиденциальности', 'Заголовок политики конфиденциальности'),
  ('privacy_policy_text', 'Мы собираем и обрабатываем персональные данные (имя, email, телефон) исключительно для связи с вами по вопросам заказа. Данные не передаются третьим лицам и хранятся в защищённой базе данных. Вы можете запросить удаление своих данных, написав на email.', 'Текст политики конфиденциальности'),
  ('consent_title', 'Согласие на обработку персональных данных', 'Заголовок согласия на обработку'),
  ('consent_text', 'Нажимая кнопку отправки формы, вы даёте согласие на обработку указанных вами персональных данных в соответствии с ФЗ-152 «О персональных данных». Ваши данные используются исключительно для связи по заявке и не передаются третьим лицам.', 'Текст согласия на обработку данных'),
  ('og_image', 'https://artemmaka.lovable.app/og-image.png', 'URL OG-изображения для соцсетей')
